def find(lst, son, l, r):    if r >= l:        mid = l + (r - l) // 2        if lst[mid] == son:            if mid + 1 < len(lst) and lst[mid + 1] == son:                return find(lst, son, mid + 1, r)            return mid        elif lst[mid] < son:            return find(lst, son, mid + 1, r)        else:            return find(lst, son, l, mid - 1)    else:        return 'Topilmadi'ls = [1, 2, 3, 4, 5, 6, 7, 7, 7]print(find(ls, 7, 0, len(ls)-1))