class Hour:    def __init__(self, hour, minute, second):        self.hour = hour        self.min = minute        self.sec = second    def plus(self, hour):        if self.hour + hour > 23:            self.hour %= 24        if self.hour == 24:            self.hour = 0        print(f"{self.hour:02d}:{self.min:02d}:{self.sec:02d}")class Minute:    def __init__(self, hour, minute, second):        self.hour = hour        self.min = minute        self.sec = second    def plus(self, minute):        if self.min + minute > 59:            self.min = self.min + minute - 60            self.hour = self.hour + 1        if self.hour == 24:            self.hour = 0        if self.min == 60:            self.min = 0        print(f"{self.hour:02d}:{self.min:02d}:{self.sec:02d}")class Second:    def __init__(self, hour, minute, second):        self.hour = hour        self.min = minute        self.sec = second    def plus(self, second):        if self.sec + second > 59:            self.sec = self.sec + second - 60            self.min = self.min + 1            self.hour = self.hour + 1        if self.hour== 24:            self.hour = 0        if self.min == 60:            self.min = 0        if self.sec == 60:            self.sec = 0        print(f"{self.hour:02d}:{self.min:02d}:{self.sec:02d}")time = Hour(10, 20, 30)time.plus(120)time1 = Minute(10, 20, 30)time1.plus(120)time2 = Second(23, 59, 59)time2.plus(1)