class Book:    def __init__(self, name, muallif, price, nashriyot):        self.name = name        self.muallif = muallif        self.price = price        self.nashriyot = nashriyot        self.check()    def check(self):        if ord(self.name[0]) in range(65, 72) or ord(self.name[0]) in range(97, 104):            print(f"{self.name} - {self.muallif} - {self.price} - {self.nashriyot}")book1 = Book('Jhaytonvachchaning nayranglari', 'Xudoyberdi', 1022, 'Hilol')book2 = Book("co'cha bolalari", "Cbaerdoaas", 1234, "Builders")book3 = Book("Bo'rder borma", "Juliyaku", 1215, "Loyitnu")book4 = Book("Doronavirus", "Koronavirus", 3209, "Polinu")book5 = Book("Lukamenda kachega", "Folukam", 67209, "Ilunmaka")