class Laptop:    def __init__(self, name, RAM, price, processor):        self.name = name        self.RAM = RAM        self.price = price        self.processor = processor        self.show()    def show(self):        if 16 > self.RAM > 4:            print(f"{self.name} - {self.RAM} - {self.price} - {self.processor}")laptop1 = Laptop('HP', 32, 7000, 'Intel')laptop2 = Laptop('Macbook', 6, 2000, 'Intel')laptop3 = Laptop('Asus', 8, 1500, 'AMD')laptop4 = Laptop('Dell', 12, 2500, 'Intel')