class Student:    def __init__(self, name, age):        self.name = name        self.age = ageclass Course:    def __init__(self, teacher, name_of_course):        self.teacher = teacher        self.name_of_course = name_of_course        self.students = []        self.number_students = 0    def add(self, student):        self.students.append([student.name, student.age])        self.number_students += 1    def delate(self, student):        if [student.name, student.age] in self.students:            self.students.remove([student.name, student.age])            self.number_students -= 1    def show_students(self):        print(f"Teacher : {self.teacher}\t|\tCourse : {self.name_of_course}")        print("---------------------------------")        for student in self.students:            print(f"Name : {student[0]}\t|\tAge : {student[1]}")        print('\n\n')Flutter = Course("Saud", "Flutter")Backend = Course("Scott", "AI")Anvar = Student("Anvar", 20)Alibek = Student("Alibek", 14)Alibek1 = Student("Alibek", 14)Farruxbek = Student("Farrux", 15)Ozodbek = Student("Ozodbek", 16)Backend.add(Farruxbek)Backend.add(Ozodbek)Flutter.add(Anvar)Flutter.add(Alibek)Flutter.add(Alibek1)Backend.delate(Alibek)Backend.delate(Ozodbek)Flutter.show_students()Backend.show_students()