class Character:    def __init__(self, name, health, damage):        self.name = name        self.health = health        self.damage = damage    def describe(self):        print(f"My name is {self.name}\nI have {self.health} health and {self.damage} damage\n\n")    def attack(self, other_shit):        other_shit.health -= self.damageclass Monster(Character):    def __init__(self, name, damage, level):        super().__init__(name,  damage, level)        self.damage = damage        self.level = level        self.health = level * 100class Hero(Character):    def __init__(self, name, health, xp):        super().__init__(name, health, xp)        self.xp = xp        self.damage = self.xp * 10    def attack(self, other_shit):        super().attack()aurora = Hero("Aurora", 250, 2)aurora.describe() # My name is Aurora. I have 250 health and 20 damagespark = Monster("Spark", 50, 3)spark.describe() # My name is Spark. I have 300 health and 50 damageaurora.attack(spark)spark.describe() # My name is Spark. I have 280 health and 50 damage