import time as timport osa=0s=100while(1):    if a==s: break    os.system("cls")    if (a>10 and a<20) or (a>30 and a<40) or (a>50 and a<60) or (a>70 and a<80):        print(a*"\033[91m ","⚽️",(s-a)*" ","+---+",sep="")        print(a*" ","  ",(s-a)*" ","|   |",sep="")    else:        if a==99:            print(a*"\033[92m ","  ",(s-a)*" ","+---+",sep="")            print(a*" ","  ",(s-a)*" ","|⚽️ |",sep="")        else:            print(a*" ","  ",(s-a)*" ","+---+",sep="")            print(a*" ","⚽️",(s-a)*" ","|   |",sep="")    t.sleep(0.02)    a+=1