list1 = []class Int:    def __init__(self, info):        self.info = info        list1.append(int(self.info))class Float(Int):    def __init__(self, info):        super().__init__(info)        list1.append(float(self.info))class Bool(Float):    def __init__(self, info):        super().__init__(info)        list1.append(bool(self.info))class Str(Bool):    def __init__(self, info):        super().__init__(info)        list1.append(str(self.info))sh = Str(input(""))print(list1)