class Telegram:    def __init__(self, user_name):        self.user_name = user_name        self.send_chat_text = None        self.accept_chat_text = None        self.chat_status = ""        self.chat_time = None    def send(self, whom,  msg):        whom.accept_chat_text = msg        self.chat_status = "SENDING"        print(f"{self.user_name} {self.chat_status} {msg} --> {whom.user_name}")    def read(self):        self.chat_status = "READING"        print(f"{self.user_name} {self.chat_status} --> {self.accept_chat_text}")    def delete(self):        self.chat_status = "DELETING"        print(f"{self.user_name} {self.chat_status} {self.accept_chat_text}")        self.accept_chat_text = ""Farrukh = Telegram("Farruxbek")Qahhor = Telegram("Qahhor")Farrukh.send(Qahhor, "Salom")Qahhor.read()Qahhor.send(Farrukh, "Yaxshi")Farrukh.delete()