import sysfrom PyQt5.QtWidgets import (QApplication,QWidget, QLabel, QLineEdit, QPushButton)from PyQt5.QtGui import QFontapp = QApplication(sys.argv)window = QWidget()window.setWindowTitle("My App")window.setGeometry(200, 100, 900, 500)input = QLineEdit(window)input.move(50, 50)input.setFont(QFont("Times", 25))yozuv = QLabel("Javob : ", window)yozuv.move(50, 300)yozuv.setFont(QFont("Times", 25))yozuv = QLabel("", window)yozuv.move(180, 300)yozuv.setFont(QFont("Times", 25))ok = QPushButton("Round", window)ok.move(100, 200)ok.setFont(QFont("Tomes", 25))def send():    str1 = round(float(input.text()))    yozuv.setText(str(str1))    yozuv.adjustSize()ok.clicked.connect(send)window.show()sys.exit(app.exec_())