import sysfrom PyQt5.QtWidgets import (QApplication,QWidget, QLabel, QLineEdit, QPushButton)from PyQt5.QtGui import QFontapp = QApplication(sys.argv)window = QWidget()window.setWindowTitle("My App")window.setGeometry(200, 100, 900, 500)input1 = QLineEdit(window)input1.move(50, 50)input1.setFont(QFont("Times", 25))yozuv = QLabel("Javob : ", window)yozuv.move(50, 250)yozuv.setFont(QFont("Times", 25))yozuv = QLabel("", window)yozuv.move(180, 250)yozuv.setFont(QFont("Times", 25))ok = QPushButton("Hisoblash", window)ok.move(100, 150)ok.setFont(QFont("Tomes", 25))def send():    a, b, c = list(map(int, input1.text().split()))    d = (b ** 2) - 4 * a * c    if d < 0:        txt = "Bo'sh to'plam"    else:        d = d ** 0.5        x1 = (b + d) / (a * 2)        x2 = (b - d) / (a * 2)        txt = f"X1 = {x1}\nX2 = {x2}"    yozuv.setText(txt)    yozuv.adjustSize()ok.clicked.connect(send)window.show()sys.exit(app.exec_())