import sysimport random as rfrom PyQt5.QtWidgets import (QApplication,QWidget, QLabel, QLineEdit, QPushButton)from PyQt5.QtGui import QFontlsts = ["Kuch adalotdadir", "Javob qaytarmalik eng yaxshi Javob", "Muvaffaqiyatga erishish oson emas,\nLekin Oddiy",        "Avval o'yla, Keyin so'yla"]colors = ["red", "blue", "green", "brown", "purple", "indigo"]app = QApplication(sys.argv)window = QWidget()window.setWindowTitle("My App")window.setGeometry(200, 100, 1100, 500)window.setStyleSheet(f"background-color:lightblue;")yozuv = QLabel(lsts[-1], window)yozuv.move(180, 50)yozuv.setFont(QFont("Times", 25))yozuv.setStyleSheet("border-radius:20px;border:2px solid black;font-size:50px;cursor:pointer;padding:10px;color:indigo;")ok = QPushButton("Update", window)ok.move(280, 300)ok.setFont(QFont("Tomes", 20))ok.setStyleSheet("border-radius:20px;border:2px solid black;font-size:50px;cursor:pointer;padding:10px;color:indigo;")def send():    ls = r.randint(0, len(lsts)-1)    cl = r.randint(0, len(colors)-1)    print(ls)    yozuv.setText(lsts[ls])    yozuv.adjustSize()    yozuv.setStyleSheet(f"border-radius:20px;border:2px solid black;font-size:50px;cursor:pointer;padding:10px;color:{colors[cl]};")ok.clicked.connect(send)window.show()sys.exit(app.exec_())