import sysfrom PyQt5.QtWidgets import (QApplication,QWidget, QLabel, QLineEdit, QPushButton)from PyQt5.QtGui import QFontdef intWord(num):    d = {1: 'bir', 2: 'ikki', 3: 'uch', 4: "to'rt", 5: 'besh', 6: 'olti', 7: 'yetti', 8: 'sakkiz', 9: "to'qqiz",         10: "o'n",         20: 'yigirma', 30: "o'ttiz", 40: 'qirq', 50: 'ellik', 60: 'oltmish', 70: 'yetmish', 80: 'sakson',         90: "to'qson"}    ming, million, milliard = 1000, 1000000, 1000000000    if num < 10:        return d[num]    elif num < 100:        return d[num] if num % 10 == 0 else d[num // 10 * 10] + ' ' + d[num % 10]    elif num < ming:        return d[num // 100] + ' yuz ' if num % 100 == 0 else d[num // 100] + ' yuz ' + intWord(num % 100)    elif num < million:        return intWord(num // ming) + ' ming' if num % ming == 0 else intWord(num // ming) + ' ming ' + intWord(            num % ming)    elif num < milliard:        return intWord(num // ming) + ' million ' if num % ming == 0 else intWord(num // ming) + ' million  ' + intWord(            num % ming)app = QApplication(sys.argv)window = QWidget()window.setWindowTitle("My App")window.setGeometry(200, 100, 800, 500)window.setStyleSheet(f"background-color:lightblue;")input1 = QLineEdit("", window)input1.move(180, 50)input1.setFont(QFont("Times", 25))input1.setStyleSheet("border-radius:20px;border:2px solid black;font-size:50px;cursor:pointer;padding:10px;color:indigo;")ok = QPushButton("Number", window)ok.move(280, 150)ok.setFont(QFont("Tomes", 20))ok.setStyleSheet("border-radius:20px;border:2px solid black;font-size:50px;cursor:pointer;padding:10px;color:indigo;")yozuv = QLabel(" ", window)yozuv.move(50, 300)yozuv.setFont(QFont("Times", 50))def send():    int1 = intWord(int(input1.text()))    yozuv.setText(int1.capitalize())    yozuv.adjustSize()    yozuv.setStyleSheet("border-radius:20px;")ok.clicked.connect(send)window.show()sys.exit(app.exec_())