from PyQt5.QtWidgets import (QApplication,QWidget, QLabel, QLineEdit, QPushButton, QMessageBox)from PyQt5.QtGui import QFontimport sysapp = QApplication(sys.argv)window = QWidget()window.resize(1000, 600)window.setWindowTitle("My Homework")window.setStyleSheet("background-color:lightblue")def alert_msg():    msg = QMessageBox()    msg.setText("Kallangizga qoyil !!!")    msg.setWindowTitle("XATOLIK !!!")    msg.setIcon(QMessageBox.Critical)    x = msg.exec_()#####################  TASK 1  #########################txt = QLabel("Task 1 : ", window)txt.move(10, 40)txt.setFont(QFont("Times", 20))Info = {"Name":"A", "SurName":"Toshmatov", "Sharif":"Beshmatovich", "Year":"2023.13.32"}Name = QLabel(Info["Name"], window)Name.move(150, 40)Name.setFont(QFont("Times", 20))x = Name.x()Surname = QLabel(Info["SurName"], window)Surname.move(x + len(Info["Name"]) * 15 + 10 , 40)Surname.setFont(QFont("Times", 20))x = Surname.x()Surname = QLabel(Info["Sharif"], window)Surname.move(x + len(Info["SurName"]) * 15 + 10 , 40)Surname.setFont(QFont("Times", 20))#####################  TASK 2  #########################txt = QLabel(f"{'-'*100}\nTask 2 : ", window)txt.move(10, 70)txt.setFont(QFont("Times", 20))style = "background-color:grey;border:2px dashed red;height:50px;border-radius:15px;width:130px"style_inp = "background-color:lightgrey;border-radius:15px;border:2px solid black;padding:2px;font-style:italic;"stl_amal = "background-color:orange;border-radius:17px;border: 3px solid red;width:50px;height:40px;font-size:40px;padding:3px;"ButName = QPushButton("Name", window)ButName.move(150, 100)ButName.setFont(QFont("Times", 20))ButName.setStyleSheet(style)ButsurName = QPushButton("SurName", window)ButsurName.move(300, 100)ButsurName.setFont(QFont("Times", 20))ButsurName.setStyleSheet(style)ButYear = QPushButton("Year", window)ButYear.move(450, 100)ButYear.setFont(QFont("Times", 20))ButYear.setStyleSheet(style)Button = QLabel("\t\t\t", window)Button.move(200, 200)Button.setFont(QFont("Times", 20))Button.setStyleSheet(f"{style};")def run(a):    Button.setText(f"{a} --> {Info[a]}")    Button.setStyleSheet(f"{style};")ButName.clicked.connect(lambda: run(ButName.text()))ButsurName.clicked.connect(lambda: run(ButsurName.text()))ButYear.clicked.connect(lambda: run(ButYear.text()))#####################  TASK 3  #########################txt = QLabel(f"{'-'*100}\nTask 3 : ", window)txt.move(10, 270)txt.setFont(QFont("Times", 20))number1 = QLineEdit(window)number1.move(150, 300)number1.setFont(QFont("Times", 20))number1.setPlaceholderText("First Number...")number1.setStyleSheet(f"{style_inp};")number2 = QLineEdit(window)number2.move(400, 300)number2.setFont(QFont("Times", 20))number2.setPlaceholderText("Second Number...")number2.setStyleSheet(f"{style_inp};")qosh = QPushButton("+", window)qosh.move(100, 370)qosh.setFont(QFont("Times", 20))qosh.setStyleSheet(stl_amal)ayir = QPushButton("-", window)ayir.move(200, 370)ayir.setFont(QFont("Times", 20))ayir.setStyleSheet(stl_amal)kopay = QPushButton("*", window)kopay.move(300, 370)kopay.setFont(QFont("Times", 20))kopay.setStyleSheet(stl_amal)bol = QPushButton("/", window)bol.move(400, 370)bol.setFont(QFont("Times", 20))bol.setStyleSheet(stl_amal)qold = QPushButton("%", window)qold.move(500, 370)qold.setFont(QFont("Times", 20))qold.setStyleSheet(stl_amal)daraj = QPushButton("**", window)daraj.move(600, 370)daraj.setFont(QFont("Times", 20))daraj.setStyleSheet(stl_amal)answer = QLabel("JAVOB : ", window)answer.move(220, 450)answer.setFont(QFont("Times", 25))result = QLabel("\t\t\t", window)result.move(350, 450)result.setFont(QFont("Times", 25))def run2(a):    x = number1.text().strip()    y = number2.text().strip()    try:        if float(x):            javob = str(eval(f"{x} {a} {y}"))            result.setText(javob[:13])    except:        alert_msg()qosh.clicked.connect(lambda: run2(qosh.text()))ayir.clicked.connect(lambda: run2(ayir.text()))bol.clicked.connect(lambda: run2(bol.text()))kopay.clicked.connect(lambda: run2(kopay.text()))qold.clicked.connect(lambda: run2(qold.text()))daraj.clicked.connect(lambda: run2(daraj.text()))window.show()sys.exit(app.exec_())