from PyQt5.QtWidgets import QApplication, QHBoxLayout, QPushButton, QWidget, QLabelfrom PyQt5.QtGui import QFontimport sysclass Task(QWidget):    def __init__(self):        super().__init__()        self.resize(500, 500)        self.start()    def font(self, obj, x, y):        obj.setFont(QFont("Times", 15))        obj.move(x, y)    def run(self):        if self.Name.text() not in self.label.text():            self.label.setText(self.label.text() + ' ' + self.Name.text())    def run1(self):        print('2asd')        self.label.setText(self.SurName.text())    def start(self):        hbox = QHBoxLayout(self)        self.label = QLabel("\t\t\t\t", self)        self.font(self.label, 100, 100)        self.label.adjustSize()        self.Name = QPushButton("Farruxbek", self)        self.font(self.Name, 100, 100)        self.Name.clicked.connect(self.run)        hbox.addWidget(self.Name)        self.SurName = QPushButton("Nozimov", self)        self.font(self.SurName, 200, 100)        self.SurName.clicked.connect(self.run1)        hbox.addWidget(self.SurName)app = QApplication(sys.argv)windows = Task()windows.show()sys.exit(app.exec_())