from PyQt5.QtWidgets import QApplication, QHBoxLayout, QPushButton, QWidget, QLabel, QComboBoxfrom PyQt5.QtGui import QFontimport sysclass Task(QWidget):    def __init__(self):        super().__init__()        self.resize(500, 500)        self.start()    def font(self, obj, x, y):        obj.setFont(QFont("Times", 15))        obj.move(x, y)    def evalute(self):        if self.com1.currentText() == self.com2.currentText():            self.label.setText('\t0\t')        else:            self.label.setText('\t1\t')    def start(self):        self.com1 = QComboBox(self)        self.font(self.com1, 130, 120)        self.com1.addItems(['1  ', '0  '])        self.com2 = QComboBox(self)        self.font(self.com2, 300, 120)        self.com2.addItems(['1  ', '0  '])        self.SurName = QPushButton("Evalute", self)        self.SurName.clicked.connect(self.evalute)        self.font(self.SurName, 200, 200)        self.label = QLabel("\t0\t", self)        self.font(self.label, 150, 300)app = QApplication(sys.argv)windows = Task()windows.show()sys.exit(app.exec_())