# # ====================== TASK 1 ======================# # RGB and HEX color converterdef rgb_and_hex(smth):    try:        if smth.strip()[0] == '#'  and len(smth.strip()) == 7:            smth = smth.strip()[1:]            for i in range(len(smth)):                if smth[i].lower() not in '0123456789abcdef':                    smth = smth[:i] + 'F' + smth[i+1:]            return f"rgb{tuple(int(smth[i:i + 2], 16) for i in range(0, len(smth), 2))}"        a = list(map(lambda x: int(x.strip()), smth[smth.find('(') + 1:smth.find(')')].split(',')))        if a[2] > 255:            a[1], a[2] = a[1] + (a[2] - 255), 255        if a[1] > 255:            a[0], a[1] = a[0] + (a[1] - 255), 255        if a[0] > 255:            a[0] = 255        for i in range(len(a)):            if a[i] < 0:                a[i] = 0        return '#%02x%02x%02x'.upper() % (a[0], a[1], a[2])    except:        return "TO'G'RI KIRITING !!!"print(rgb_and_hex(input('=> ')))