# ====================== TASK 2 ======================# LEAST DIFFERENCEdef least_difference(smth):    try:        smth = int(smth.strip())        lst = []        for i in range(len(str(smth))):            a = 0            for j in range(len(str(smth))):                a += abs(int(str(smth)[i]) - int(str(smth)[j]))            lst.append(a)        return f"{str(smth)[lst.index(min(lst))] * len(str(smth))} --> {min(lst)}"    except:        return "AQLINGIZGA QOIL !!!"print(least_difference(input('=> ')))