# ====================== TASK 4 ======================# POLISH NOTATIONa = input('=> ')def polish_notation(a):    def tekshirish(str,num,num2, amal=0):        return eval(f'{num} {str[amal]} {num2}')    if "(" in a:        b = a.index("(")        d = tekshirish(a, int(a[b+3]), int(a[b+5], b+1))        if a[-1].isdigit():            print(tekshirish(a, d, int(a[-1])))        elif a[2].isdigit():            print(tekshirish(a, int(a[2]), d))    return tekshirish(a, int(a[2]), int(a[4]))print(polish_notation(a))